export default{init:function(){let e,t=document.querySelectorAll("[data-d-badge]");for(e=0;e<t.length;e++)t[e].classList.add("d-badge")},remove:function(e){let t=document.querySelector(`#${e}`);t&&t.classList.remove("d-badge")},removeAll:function(){let e,t=document.querySelectorAll("[data-d-badge]");for(e=0;e<t.length;e++)t[e].classList.remove("d-badge")},set:function(e,t){let d=document.querySelector("#"+e);d&&(d.classList.add("d-badge"),d.setAttribute("data-d-badge",t.toString()))}};
