export default{init:function(){let e,t=document.querySelectorAll("[data-lb-badge]");for(e=0;e<t.length;e++)t[e].classList.add("lb-badge")},remove:function(e){let t=document.querySelector(`#${e}`);t&&t.classList.remove("lb-badge")},removeAll:function(){let e,t=document.querySelectorAll("[data-lb-badge]");for(e=0;e<t.length;e++)t[e].classList.remove("lb-badge")},set:function(e,t){let l=document.querySelector(`#${e}`);l&&(l.classList.add("lb-badge"),l.setAttribute("data-lb-badge",t.toString()))}};
